// too slow

struct Solution;

impl Solution {
    pub fn number_of_substrings(s: String) -> i32 {
        let mut squares = [0; 200];
        for i in 0..200 { squares[i] = i * i }

        let len = s.len();
        let mut walk = Vec::with_capacity(len + 1);
        let mut sum = 0;
        walk.push(sum);
        for c in s.chars() {
            if c == '0' { sum += 1 }
            walk.push(sum);
        }

        let mut res = 0;
        for lo in 0..len {
            let mut hi = lo + 1;
            while hi <= len {
                let zeroes = walk[hi] - walk[lo];
                if zeroes >= 200 { break }
                let ones = (hi - lo) - zeroes;

                // enough ones
                // maximum safe jump
                if ones >= squares[zeroes] {
                    let diff = ones - squares[zeroes];
                    let safe_jump = 1 + Self::root(&squares, squares[zeroes] + diff) - zeroes;
                    let jump = safe_jump.min(len + 1 - hi);
                    res += jump;
                    hi += jump;
                    continue;
                }

                // not enough ones
                // minimum jump to fill needed ones
                hi += squares[zeroes] - ones;
            }
        }

        res as i32
    }

    fn root(squares: &[usize; 200], n: usize) -> usize {
        let mut lo = 0;
        let mut hi = 199;
        while lo < hi {
            let m = lo + hi + 1 >> 1;
            if squares[m] > n {
                hi = m - 1;
            } else {
                lo = m;
            }
        }
        lo
    }
}

#[test]
fn test_1() {
    let actual = Solution::number_of_substrings(String::from("00011"));
    let expected = 5;
    assert_eq!(actual, expected);
}

#[test]
fn test_2() {
    let actual = Solution::number_of_substrings(String::from("101101"));
    let expected = 16;
    assert_eq!(actual, expected);
}

#[test]
fn test_3() {
    let actual = Solution::number_of_substrings(String::from(""));
    let expected = 117517410;
    assert_eq!(actual, expected);
}
