// https://leetcode.com/problems/count-the-number-of-substrings-with-dominant-ones/submissions/1830509514/

struct Solution;

impl Solution {
    pub fn number_of_substrings(s: String) -> i32 {
        let len = s.len();
        let mut walk = Vec::with_capacity(len + 1);
        let mut sum = 0;
        walk.push(sum);
        for c in s.chars() {
            if c == '0' { sum += 1 }
            walk.push(sum);
        }

        let mut res = 0;
        for lo in 0..len {
            let mut hi = lo + 1;
            let walk_lo = walk[lo];

            while hi <= len {
                let zeroes = walk[hi] - walk_lo;
                if zeroes >= 200 { break }
                let ones = hi - lo - zeroes;
                let sq_zeros = zeroes * zeroes;
                let diff = ones as isize - sq_zeros as isize;

                // enough ones
                // maximum safe jump
                if diff >= 0 {
                    let safe_jump = 1 + ((sq_zeros + diff as usize) as f32).sqrt().floor() as usize - zeroes;
                    let jump = safe_jump.min(len + 1 - hi);
                    res += jump;
                    hi += jump;
                    continue;
                }

                // not enough ones
                // minimum jump to fill needed ones
                hi += (-1 * diff) as usize;
            }
        }

        res as i32
    }
}

#[test]
fn test_1() {
    let actual = Solution::number_of_substrings(String::from("00011"));
    let expected = 5;
    assert_eq!(actual, expected);
}

#[test]
fn test_2() {
    let actual = Solution::number_of_substrings(String::from("101101"));
    let expected = 16;
    assert_eq!(actual, expected);
}

#[test]
fn test_3() {
    let actual = Solution::number_of_substrings(String::from(""));
    let expected = 117517410;
    assert_eq!(actual, expected);
}
