// https://leetcode.com/problems/count-the-number-of-substrings-with-dominant-ones/submissions/1831474703/

struct Solution;

impl Solution {
    pub fn number_of_substrings(s: String) -> i32 {
        let len = s.len();
        let mut izeroes = Vec::new();
        for (i, c) in s.bytes().enumerate() {
            if c == b'0' { izeroes.push(i) }
        }

        // izeroes base index
        let izlen = izeroes.len();
        let mut ibase = 0;
        let mut res = 0;

        for lo in 0..len {
            // adjust base
            while ibase < izeroes.len() && izeroes[ibase] < lo { ibase += 1 }
            let mut iz = ibase;
            let mut hi = lo;

            while hi < len {
                // move iz to point zero ahead
                while iz < izlen && izeroes[iz] <= hi { iz += 1 }

                let zeroes = iz - ibase;
                // too many zeroes
                if zeroes >= 200 { break }
                let square = zeroes * zeroes;
                let ones = hi - lo + 1 - zeroes;

                // enough ones
                if ones >= square {
                    // no more zeroes
                    if iz >= izlen {
                        res += len - hi;
                        break;
                    }
                    // jump to next zero
                    res += izeroes[iz] - hi;
                    hi = izeroes[iz];

                // not enough ones
                } else {
                    let diff = square - ones;
                    hi += diff;
                }
            }
        }

        res as i32
    }
}

#[test]
fn test_1() {
    let actual = Solution::number_of_substrings(String::from("00011"));
    let expected = 5;
    assert_eq!(actual, expected);
}

#[test]
fn test_2() {
    let actual = Solution::number_of_substrings(String::from("101101"));
    let expected = 16;
    assert_eq!(actual, expected);
}

#[test]
fn test_3() {
    let actual = Solution::number_of_substrings(String::from(""));
    let expected = 117517410;
    assert_eq!(actual, expected);
}
