// https://leetcode.com/problems/count-the-number-of-substrings-with-dominant-ones/submissions/1834527236/

struct Solution;

impl Solution {
    pub fn number_of_substrings(s: String) -> i32 {
        let mut res = 0;
        let mut pos0s = Vec::new();

        for (hi, c) in s.bytes().enumerate() {
            if c == b'0' { pos0s.push(hi) }

            if pos0s.is_empty() {
                res += hi + 1;
                continue;
            }

            let mut ones = hi - pos0s[pos0s.len() - 1];
            res += ones;

            for (i, pos) in pos0s.iter().rev().enumerate() {
                let square = (i + 1) * (i + 1);
                if square > hi { break }
                let need = if ones >= square { 0 } else { square - ones };
                let have = if i + 1 < pos0s.len() { pos - pos0s[pos0s.len() - i - 2] - 1 } else { *pos };
                if have >= need { res += have - need + 1 }
                ones += have;
            }
        }

        res as i32
    }
}

#[test]
fn test_1() {
    let actual = Solution::number_of_substrings(String::from("00011"));
    let expected = 5;
    assert_eq!(actual, expected);
}

#[test]
fn test_2() {
    let actual = Solution::number_of_substrings(String::from("101101"));
    let expected = 16;
    assert_eq!(actual, expected);
}

#[test]
fn test_3() {
    let actual = Solution::number_of_substrings(String::from(""));
    let expected = 117517410;
    assert_eq!(actual, expected);
}
